document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("icon-tooltip"),o={title:"Ваша сумма покупки составила менее 1000₽, добавьте товаров до 1000₽ , чтобы получить оптовую цену, указанную на сайте, или продолжите  оформление заказа по розничной цене",html:!0,customClass:"price-tooltip",placement:"right",offset:window.matchMedia("(min-width: 768px)").matches?[-40,0]:[-100,-152]},e=new bootstrap.Tooltip(t,o);t.addEventListener("mouseenter",()=>e.show()),t.addEventListener("mouseleave",()=>e.hide())});
