document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("icon-tooltip"),e={title:"Ваша сумма покупки составила менее 1000₽, добавьте товаров до 1000₽ , чтобы получить оптовую цену, указанную на сайте, или продолжите  оформление заказа по розничной цене",html:!0,customClass:"price-tooltip",placement:"right",offset:[-40,0]};window.matchMedia("max-width: 767px").matches&&(e.offset=[-100,-152]);const o=new bootstrap.Tooltip(t,e);t.addEventListener("mouseenter",()=>o.show()),t.addEventListener("mouseleave",()=>o.hide())});
